// src/api.js

const API_BASE_URL = "http://localhost:9000";
// Change to your Render/Railway backend URL after deployment

// Function to submit the lead form data
export const submitLeadForm = async (formData) => {
  try {
    const res = await fetch(`${API_BASE_URL}/api/leads`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });

    // If response is not OK (status 200–299), throw an error
    if (!res.ok) {
      const errorData = await res.json().catch(() => ({}));
      throw new Error(errorData.message || "Request failed");
    }

    // Return JSON data from backend
    return await res.json();
  } catch (err) {
    console.error("❌ API Error:", err.message);
    throw err; // Pass the error to the calling function
  }
};
