import React, { useContext } from "react";
import Hero from "./components/Hero";
import HowItWorks from "./components/HowItWorks";
import Features from "./components/Features";
import LeadForm from "./components/LeadForm";
import AdminLeads from "./components/AdminLeads"; // Import if used here or route it
import { ThemeContext } from "./context/ThemeContext";

function App() {
  const { darkMode, toggleDarkMode } = useContext(ThemeContext);

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 transition-colors duration-500">
      <header className="p-4 flex justify-end bg-gray-100 dark:bg-gray-800">
        <button
          onClick={toggleDarkMode}
          className="text-gray-800 dark:text-gray-200 bg-gray-200 dark:bg-gray-700 px-4 py-2 rounded"
          aria-label="Toggle dark mode"
        >
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
      </header>

      {/* Your regular app content */}
      <main className="p-4 text-gray-900 dark:text-gray-100">
        <Hero />
        <HowItWorks />
        <Features />
        <LeadForm />
        {/* Render AdminLeads on /admin/leads route or wherever */}
      </main>
    </div>
  );
}

export default App;
